import React, { FC, MutableRefObject, useRef } from 'react';
import { useRectangles } from '../hooks/useRectangles';
import "./Canvas.css";

interface ViewProps {
  create(): void,
  ref: MutableRefObject<null>
}

const View : FC<ViewProps> = ({ create, ...props }) => (
  <>
    <button onClick={create} value="Create Rectangle" />
    <canvas {...props} />
  </>
);

const Canvas : FC = () => {

  const canvasRef = useRef(null);

  const { createRectangle } = useRectangles();

  const hookProps = {
    create: () => createRectangle(canvasRef.current),
  };

  return (
    <View ref={canvasRef} {...hookProps} />
  );
};

export default Canvas;
